import React, { useState, useEffect } from "react";
import axios from "axios";
import { db } from "./firebase";
import {
  collection,
  addDoc,
  getDocs,
  serverTimestamp,
} from "firebase/firestore";

function App() {
  const [text, setText] = useState("");
  const [summary, setSummary] = useState("");
  const [quiz, setQuiz] = useState("");
  const [saved, setSaved] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleProcess = async () => {
    if (!text.trim()) {
      alert("Please paste some notes first.");
      return;
    }

    setLoading(true);

    try {
      const res = await axios.post("http://localhost:5000/process", { text });

      if (!res.data.summary) throw new Error("No summary returned.");
      if (!res.data.quiz) throw new Error("No quiz returned.");

      setSummary(res.data.summary);
      setQuiz(res.data.quiz);

      try {
        await addDoc(collection(db, "summaries"), {
          original: text,
          summary: res.data.summary,
          quiz: res.data.quiz,
          createdAt: serverTimestamp(),
        });
      } catch (firestoreError) {
        console.warn("Firestore save failed, but processing succeeded.", firestoreError);
      }

      fetchSaved();
    } catch (err) {
      console.error("REAL BACKEND ERROR:", err);
      alert("Backend failed. Check server or API key.");
    } finally {
      setLoading(false);
    }
  };

  const fetchSaved = async () => {
    try {
      const snap = await getDocs(collection(db, "summaries"));
      const items = snap.docs.map((d) => ({ id: d.id, ...d.data() }));

      items.sort((a, b) => (b.createdAt?.seconds || 0) - (a.createdAt?.seconds || 0));

      setSaved(items);
    } catch (err) {
      console.error("Firestore read error", err);
    }
  };

  useEffect(() => {
    fetchSaved();
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-6 flex justify-center font-sans">
      <div className="w-full max-w-3xl bg-white shadow-xl rounded-2xl p-8 space-y-6">
        <div className="text-center">
          <h1 className="text-3xl font-bold text-indigo-600">üß† Smart Study Assistant</h1>
          <p className="text-gray-500">Paste notes ‚Üí Get Summary + Quiz</p>
        </div>

        <div>
          <label className="text-gray-600 font-medium">Paste your lecture notes:</label>
          <textarea
            value={text}
            onChange={(e) => setText(e.target.value)}
            placeholder="Paste your notes here..."
            className="w-full h-40 mt-2 p-4 border rounded-xl focus:ring-2 focus:ring-indigo-400 outline-none"
          />
        </div>

        <div className="flex gap-4">
          <button
            onClick={handleProcess}
            disabled={loading}
            className="flex-1 bg-indigo-600 hover:bg-indigo-700 text-white py-3 rounded-xl shadow-md transition disabled:bg-gray-400"
          >
            {loading ? "Processing..." : "Generate Summary & Quiz"}
          </button>

          <button
            onClick={fetchSaved}
            className="flex-1 bg-gray-200 hover:bg-gray-300 py-3 rounded-xl shadow"
          >
            Refresh Saved
          </button>
        </div>

        <div>
          <h3 className="text-xl font-semibold text-indigo-700">üìÑ Summary</h3>
          <div className="bg-gray-50 p-4 rounded-xl border mt-2 text-gray-700 whitespace-pre-wrap">
            {summary || "No summary yet."}
          </div>
        </div>

        <div>
          <h3 className="text-xl font-semibold text-indigo-700">üìù Quiz</h3>
          <div className="bg-gray-50 p-4 rounded-xl border mt-2 text-gray-700 whitespace-pre-wrap">
            {quiz || "No quiz yet."}
          </div>
        </div>

        <div>
          <h3 className="text-xl font-semibold text-indigo-700">üíæ Saved Entries</h3>

          {saved.length === 0 && (
            <p className="text-gray-500 mt-2">No saved entries yet.</p>
          )}

          <div className="space-y-4 mt-4">
            {saved.map((s) => (
              <div key={s.id} className="bg-gray-50 border rounded-xl p-4 shadow-sm">
                <p className="text-xs text-gray-500">
                  <strong>Saved:</strong> {s.createdAt?.toDate ? s.createdAt.toDate().toLocaleString() : ""}
                </p>

                <div className="mt-3">
                  <strong className="text-gray-700">Summary</strong>
                  <div className="bg-white border p-3 rounded-xl mt-1 whitespace-pre-wrap text-gray-700">
                    {s.summary}
                  </div>
                </div>

                <div className="mt-3">
                  <strong className="text-gray-700">Quiz</strong>
                  <div className="bg-white border p-3 rounded-xl mt-1 whitespace-pre-wrap text-gray-700">
                    {s.quiz}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
