import React, { useState, useEffect } from "react";
import axios from "axios";
import { db } from "./firebase";
import {
  collection,
  addDoc,
  getDocs,
  serverTimestamp,
} from "firebase/firestore";
import { motion } from "framer-motion";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Loader2 } from "lucide-react";

function App() {
  const [text, setText] = useState("");
  const [summary, setSummary] = useState("");
  const [quiz, setQuiz] = useState("");
  const [saved, setSaved] = useState([]);
  const [loading, setLoading] = useState(false);

  // ---------------------------
  // PROCESS NOTES (Summary + Quiz)
  // ---------------------------
  const handleProcess = async () => {
    if (!text.trim()) {
      alert("Please paste some notes first.");
      return;
    }

    setLoading(true);

    try {
      // 1. Call backend
      const res = await axios.post("http://localhost:5000/process", { text });

      if (!res.data.summary) throw new Error("No summary returned.");
      if (!res.data.quiz) throw new Error("No quiz returned.");

      setSummary(res.data.summary);
      setQuiz(res.data.quiz);

      // 2. Save to Firestore (safe)
      try {
        await addDoc(collection(db, "summaries"), {
          original: text,
          summary: res.data.summary,
          quiz: res.data.quiz,
          createdAt: serverTimestamp(),
        });
      } catch (firestoreError) {
        console.warn(
          "Firestore save failed, but processing succeeded.",
          firestoreError
        );
      }

      fetchSaved();
    } catch (err) {
      console.error("REAL BACKEND ERROR:", err);
      alert("Backend failed. Check server or API key.");
    } finally {
      setLoading(false);
    }
  };

  // ---------------------------
  // LOAD SAVED ITEMS FROM FIRESTORE
  // ---------------------------
  const fetchSaved = async () => {
    try {
      const snap = await getDocs(collection(db, "summaries"));
      const items = snap.docs.map((d) => ({ id: d.id, ...d.data() }));

      // Sort by latest timestamp
      items.sort(
        (a, b) => (b.createdAt?.seconds || 0) - (a.createdAt?.seconds || 0)
      );

      setSaved(items);
    } catch (err) {
      console.error("Firestore read error", err);
    }
  };

  // Load saved summaries on page load
  useEffect(() => {
    fetchSaved();
  }, []);

  return (
  <div className="min-h-screen bg-gray-100 p-6 flex flex-col items-center gap-6">
<motion.h1 initial={{ opacity: 0, y: -10 }} animate={{ opacity: 1, y: 0 }} className="text-4xl font-bold text-gray-800">
üß† Smart Study Assistant
</motion.h1>


<Card className="w-full max-w-3xl shadow-lg">
<CardContent className="p-6 flex flex-col gap-4">
<p className="text-sm text-gray-600">Paste your lecture notes below:</p>
<textarea
value={text}
onChange={(e) => setText(e.target.value)}
placeholder="Paste your notes here..."
className="w-full h-40 p-3 border rounded-xl shadow-sm focus:outline-none focus:ring-2 focus:ring-purple-400"
/>


<div className="flex gap-3">
<Button onClick={handleProcess} disabled={loading} className="px-6">
{loading ? <Loader2 className="animate-spin h-5 w-5" /> : "Generate Summary & Quiz"}
</Button>
<Button variant="secondary" onClick={fetchSaved}>Refresh Saved</Button>
</div>
</CardContent>
</Card>


<div className="grid grid-cols-1 md:grid-cols-2 gap-6 w-full max-w-5xl">
<Card className="shadow-lg">
<CardContent className="p-5">
<h3 className="text-xl font-semibold mb-3">üìÑ Summary</h3>
<div className="text-sm text-gray-700 whitespace-pre-wrap">{summary || "No summary yet."}</div>
</CardContent>
</Card>


<Card className="shadow-lg">
<CardContent className="p-5">
<h3 className="text-xl font-semibold mb-3">üìù Quiz</h3>
<div className="text-sm text-gray-700 whitespace-pre-wrap">{quiz || "No quiz yet."}</div>
</CardContent>
</Card>
</div>


<div className="w-full max-w-5xl mt-6">
<h3 className="text-2xl font-semibold mb-4">üíæ Saved Entries</h3>
{saved.length === 0 && <div className="text-gray-600">No saved entries yet.</div>}


<div className="grid grid-cols-1 md:grid-cols-2 gap-6">
{saved.map((s) => (
<Card key={s.id} className="shadow">
<CardContent className="p-5 flex flex-col gap-3">
<div className="text-xs text-gray-500">
<strong>Saved:</strong> {s.createdAt?.toDate ? s.createdAt.toDate().toLocaleString() : ""}
</div>
<div>
<strong>Summary</strong>
<div className="text-sm text-gray-700 whitespace-pre-wrap">{s.summary}</div>
</div>
<div>
<strong>Quiz</strong>
<div className="text-sm text-gray-700 whitespace-pre-wrap">{s.quiz}</div>
</div>
</CardContent>
</Card>
))}
</div>
</div>
</div>
);
}
